import React, { FunctionComponent, useState } from 'react';

import Button from '@material-ui/core/Button';
import { Stopwatch } from "ts-stopwatch"

interface Props {
    breakTime: number;
    onTimePassed: () => void;
}

export const BreakScreen: FunctionComponent<Props> = ({
    breakTime,
    onTimePassed
}) => {

    const [pText, setPText] = useState("Zakończyłeś odpowiadanie na zestaw. Poczekaj 2 min i kliknij w przycisk, aby przejść do następnego zestawu.");

    const styles = {
        "text-align": "center"
    }

    const stopwatch = new Stopwatch();
    stopwatch.start();

    const onClick = () => {
        if(stopwatch.getTime() < breakTime) {
            setPText(`Jeszcze nie upłynęły 2 min. Pozostało ${Math.floor((breakTime - stopwatch.getTime()) / 1000)}s`);
        } else {
            onTimePassed();
        }
    };

    const buttonStyles = {
        "width": "100%",
        "height": "100%"
    }
    return (
        <div style={{
            width: '65%',
            textAlign: "center"
        }}>
            <p style={{fontSize: '160%', textAlign: "center", marginBottom: "5%", height: "100%"}}>{pText}</p>
            <Button variant="contained" style={{display: "inline-block", float: "none"}} onClick={onClick}>Następny zestaw</Button>
        </div>
    );
}
